/* Section About */
/* ----------------------------------------------- */
#section-about{
  margin-bottom: 2rem;
}
.about-img{
  width: 45.8rem; 
  height: 60rem;
}
.about-right h1{
  margin-top: 4rem;
  margin-bottom: 2rem;
  font-size: 2.5rem;
  font-weight: 400;
  display: inline-block;
  background-image: linear-gradient(to right bottom, #2998ff, #5643fa);
  -webkit-background-clip: text;
  color: transparent;
  transition: all .3s;
}
.about-right h1:hover{
  transform: skewY(2deg) skewX(15deg) scale(1.1);
  text-shadow: 0.5rem 1rem 2rem rgba(255, 255, 255, 0.2); 
}   
.about-right p{
  margin-bottom: 1rem;
  font-size: 1.6rem;
  font-weight: 300;
  line-height: 1.6;
  color: #777; 
  width: 83%;
}
.about-img{
  animation: cubic-bezier(0.39, 0.575, 0.565, 1);
  transform: scale(1);
  transition: all.3s;
  box-shadow: 0 1.5rem 1.5rem rgba(0, 0, 0, 0.3);
}
.about-img:hover{
  transform: scale(.8);
  box-shadow: 0 2.5rem 2.5rem rgba(0, 0, 0, 0.3);
}
/* ----------------------------------------------- */
/* Section Gallery */
/* ----------------------------------------------- */
.section-gallery h1{
  margin-top: 2rem;
  margin-bottom: 2rem;
  font-size: 3rem;
  text-align: center;
  font-weight: 700;
  background-image: linear-gradient(to right bottom, #fffafa, #fff8f8);
  -webkit-background-clip: text;
  color: transparent;
  transition: all .3s;
  backface-visibility: hidden;
}
.section-gallery h1:hover{
  transform: skewY(2deg) skewX(15deg) scale(1.1);
  text-shadow: 0.5rem 1rem 2rem rgba(0, 0, 0, 0.2); 
}
.section-gallery p{
  margin-bottom: 8rem;
  font-size: 1.3rem;
  font-weight: 300;
  line-height: 1.6;
  color: #fff; 
  text-align: center;
  font-weight: 400;
}
.section-gallery{
  background-color: #222;
}
.recent-photos {
  list-style: none;
  width: 100%;
  margin: 0;
}
.recent-photos li {
  display: block;
  float: left;
  width: 25%;
}
.recent-photo {
  width: 100%;
  height: 100%;
  margin: 0;
  overflow: hidden;
  background-color: #000;
}
.recent-photo img {
  opacity: 0.7;
  width: 100%;
  height: 100%;
  transform: scale(1.15);
  transition: transform 0.5s, opacity 0.5s;
}
.recent-photo img:hover {
  opacity: 1;
  transform: scale(1.03);
}
.center{
  position: relative;
  background-color: #222;
  margin-top: 4rem;
  margin-bottom: 2rem;
}
.center-btn{
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  margin-top: 2rem;
}
/* ----------------------------------------------- */
/* Section Form */
/* ----------------------------------------------- */
.contact-right { 
  padding-left: 5%;
  padding-right: 15%;
  
  /* margin-bottom: 5rem; */
}
.contact-right h1{
  margin-top: 4rem;
  margin-bottom: 2rem;
  font-size: 2.5rem;
  font-weight: 400;


  /* display: inline-block; */
  background-image: linear-gradient(to right bottom, #2998ff, #5643fa);
  -webkit-background-clip: text;
  color: transparent;
  transition: all .3s;
}
.contact-right h1:hover{
  transform: skewY(2deg) skewX(15deg) scale(1.1);
  text-shadow: 0.5rem 1rem 2rem rgba(255, 255, 255, 0.2); 
}
.contact-right p{
  font-size: 1.6rem;
  font-weight: 300;
  line-height: 1.6;
  color: #777;
}
.contact-right input[type=text],
.contact-right input[type=email],
.contact-right textarea{
  width: 100%;
  padding: .2rem .5rem;
  border-radius: 3px;
  border: 1px solid #ccc;
  margin-top: 1rem;
}
input::placeholder {
  font-size: 1.4rem;
}
textarea::placeholder{
  font-size: 1.4rem;
}
.contact-right textarea {
    height: 15rem;
}
*:focus {outline: none;
}
/* ----------------------------------------------- */
/* Footer */
/* ----------------------------------------------- */
footer{
  background-color: #222;
  margin-top: -11rem;
}
.center-icons{
  text-align: center;
  margin-top: 3rem;
  color: #f7f7f7;
  font-size: 1.5rem;
}
.footer-link:link, .footer-link:visited {
  text-decoration: none;
  color: #f7f7f7;
  text-transform: uppercase;
  display: inline-block;
  transition: all .2s; 
}
  
.footer-link:hover, .footer-link:active {
  color:rgba(41, 152, 255, 0.85);
  transform: rotate(5deg) scale(1.3);
 }
 .footer-copyright{
   font-size: 1.4rem;
   color: #f7f7f7;
 }
/* ----------------------------------------------- */
/* icons */
/* ----------------------------------------------- */
.fa-facebook-f:link,
.fa-twitter,
.fa-google-plus-g,
.fa-linkedin-in,
.fa-instagram,
.fa-github{
  cursor: pointer;
  transition: color .2s;
}
.fa-facebook-f:hover {
  color: #3b5998;
}
.fa-twitter:hover{
  color: #00aced;
}
.fa-google-plus-g:hover{
  color: #dd4b39;
}
.fa-linkedin-in:hover{
  color: #0077b5;
}
.fa-instagram:hover{
  color: #7232bd;
}
.fa-github:hover{
  color: #777;
}
